import React, { Component } from 'react';
import {connect} from 'react-redux';

class NoteForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            noteTitle: '',
            noteContent: ''
        }//tao state de luu du lieu tu form
    }

    isChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]:value
        });
    }// ham luu du lieu vao sate

    addData = (title, content) => {
        let item = {};
        item.noteTitle = title;
        item.noteContent = content;

        // this.props.getData(item); 
        // alert('Them thanh cong!');

        //item = JSON.stringify(item);
        this.props.addDataStore(item); // su dung reducer o trong store , dispatch ADD_DATA
        alert('update data thanh cong!');
    }// lay du lieu tu state truyen len tren app

    showDataFromStore = () => {
        console.log("abc",JSON.stringify(this.props.editNote));
    }

    render() {
        return (
            // Note Form
            <div className="col-4">
            <h3>Sửa nội dung</h3>
                {this.showDataFromStore()}
                <form>
                    <div className="form-group">
                        <label htmlFor="noteTitle">Tiêu đề Note</label>
                        <input onChange={(event) => this.isChange(event)} type="text" className="form-control" name="noteTitle" id="noteTitle" aria-describedby="noteTitle" placeholder="Tiêu đề " />
                        <small id="noteTitle" className="form-text text-muted">Điền tiêu đề vào đây</small>
                    </div>
                    <div className="form-group">
                        <label htmlFor="noteContent">Nội dung Note</label>
                        <textarea onChange={(event) => this.isChange(event)} type="text" className="form-control" name="noteContent" id="noteContent" aria-describedby="noteContent" placeholder="Nội dung" defaultValue={""} />
                        <small id="noteContent" className="form-text text-muted">Điền nội dung vào đây</small>
                    </div>
                    <button type="reset" onClick={() => this.addData(this.state.noteTitle, this.state.noteContent)} className="btn btn-primary">Lưu</button>
                </form>
            </div>
            // End NoteForm
        );
    }
}

//B1 mapStateToProps
const mapStateToProps = (state, ownProps) => {
    return {
        editNote: state.editNote,

    }
}

// NoteForm se nhan duoc this.props.test

//B2 mapDispatchToProps
const mapDispatchToProps = (dispatch, ownProps) => {
    return {
        addDataStore: (getItem) => {
            dispatch({type: "ADD_DATA",getItem})
        }
    }
}
// NoteForm se nhan duoc this.props.addDataStore

// B3 connect store
export default connect(mapStateToProps, mapDispatchToProps)(NoteForm);
