import React, { Component } from 'react';
import { Link } from 'react-router-dom';

class Nav extends Component {

    handleAddNote= (event) => {
         event.preventDefault(); //click vao khong bi chuyen trang
    }

    render() {
        return (
            <>
                {/* Menu top */}
                <nav className="navbar navbar-expand-sm navbar-dark bg-dark">
                    <Link className="navbar-brand" >Note app</Link>
                    <button className="navbar-toggler d-lg-none" type="button" data-toggle="collapse" data-target="#collapsibleNavId" aria-controls="collapsibleNavId" aria-expanded="false" aria-label="Toggle navigation" />
                    <div className="collapse navbar-collapse justify-content-end" id="collapsibleNavId">
                        <ul className="navbar-nav mt-2 mt-lg-0">
                        <li className="nav-item active">
                            <Link className="nav-link" >Home <span className="sr-only">(current)</span></Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" href="" onClick={(event) => this.handleAddNote(event)}>Thêm ghi chú</Link>
                        </li>
                        </ul>
                    </div>
                </nav>
                {/* End Menu Top */}
            </>
        );
    }
}

export default Nav;
