import {noteData} from './firebaseConnect';

let redux = require('redux');

const noteInitialState = {
    testDataConnect: 'test'
};// trang thai ban dau cua state
//reducer quan ly state va action 
const allReducer = (state = noteInitialState, action) => {
    switch (action.type) {
        case "ADD_DATA":
            //console.log('connect thanh cong add data, bien nhan duoc la:' + action.getItem);
            noteData.push(action.getItem);
            return state
        default:
            return state
    }
}

let store = redux.createStore(allReducer); //store quan ly reducer

export default store;
