import React, { Component } from 'react';
import { connect } from 'react-redux';

class ListItem extends Component {

    render() {
        return (
            <div className="card">
                <div className="card-header" role="tab" id="note1">
                    <h5 className="float-left">
                    <a data-toggle="collapse" data-parent="#noteList" href={"#noteKey-"+this.props.i} aria-expanded="true" aria-controls="noteContent1">
                        {this.props.noteTitle}
                    </a>
                    </h5>
                    <div class="float-right">
                        <button type="button" className="btn btn-info">Sửa</button>
                        <button type="button" className="btn btn-danger" onClick={() => this.props.editNote()}>Xóa</button>
                    </div>
                </div>
                <div id={"noteKey-"+this.props.i} className="collapse in" role="tabpanel" aria-labelledby="note1">
                    <div className="card-body">
                        {this.props.noteContent}
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        editStatus: state.isEdit
    }
}

const mapDispatchToProps = (dispatch, ownProps) => {
    return {
        editNote: () => {
            dispatch({
                type: "EDIT_DATA"
            })
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ListItem)
